import React, { useState } from 'react'
import classes from './MealItem.module.css'
const MealItem = (props) => {
  const [items, setItems] = useState([]);

  const onSubmitHandler = (e,id) => {
    console.log(e, id)
    e.preventDefault();

  }

  const addItemHandler = (id) => {
      setItems ((preState) => {
        const searchItem = preState.find(preState.id === id)
        let updateItems;
        if ( searchItem ) {
             alert('element found');
        }
        else 
          {updateItems.push({id:props.id , 
             name: props.name,
             description: props.description, 
             price:props.price})}
          
        return updateItems;
      })
  }

  return (
    <div 
     className={classes.wrapper}>

      <section className={classes.left}>
        <h3>{props.name}</h3>
        <h5>{props.price}</h5>
        <p>{props.description}</p>
      </section>

      <section className={classes.right}>

        <form onSubmit={() => addItemHandler(id)}>
          <label>Amount</label>
          <input type='number'/>
          <button>Add</button>
        </form>

      </section>
    </div>
  )
}

export default MealItem
