import React, { useEffect, useState } from 'react'
import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, getAuth } from 'firebase/auth';
import { auth } from '../../firebase/firebase';
 

const useAuth =() => {
  const userHandler = (userHandler, email, password) => {
       userHandler(email, password).then(userCretianel => alert(userCretianel.user) ).catch(error => alert(error.message))
  }
}

export default useAuth;
