import {BsCartPlus} from 'react-icons/bs'
import CartContext from '../../../store/cart-context';
import classes from './NavCart.module.css';

const NavCart = () => {

  const ctx = useContext(CartContext);

  let totalProduct = ctx.products.red;
  // for (let i=0; i<ctx.products.length; i++) 
  //            totalAmount+= ctx.products[i].quantity;
   


  return (
    <nav>
      {/* Login  */}
      {/* Register  */}
     <button className={classes.cart}>
       <BsCartPlus className={classes.icon}/>
       <span className={classes.badge}>{totalProduct}</span>
     </button>
    </nav>
  )
}

export default NavCart
