import React, { useContext } from 'react';
import {BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './component/Layout/Header/Header';
import Products from './component/Home/Products/Products';
import ProductItemDetails from './component/Home/Products/ProductItemDetails';
import Cart from './component/CartProduct/Cart';
import Login from './component/Auth/Login';
import Register from './component/Auth/Register';

import Modal from './component/UI/Modal/Modal';
import NotFound from './component/NotFound';
import CartContext from './store/cart-context';



const App = () => {
  
  const {items, showCart, products, } = useContext(CartContext);
  console.log(items, showCart, products);

  return (

    <Router>
      <Header/>
      {/* {showCart && <Modal/>} */}

       <Routes> 
        <Route path='/'   //props?
         element={<Products products={items.products}/>} />
          {/* path element  */}
        <Route path='/:productId' 
          element={<ProductItemDetails  
                  products={items.products}/>}/>

        <Route path='/cart' 
          element={products.length <1 ? <Navigate to='/'/> : <Cart products={products}/> }/>
        <Route path='/login' element={<Login/>}/>
        <Route path='/register' element={<Register/>}/>
       
       </Routes>
      {!error && <NotFound/>}
     </Router>
  )
}
export default App;
