import { async } from '@firebase/util';
import React from 'react'
import { Link } from 'react-router-dom'
import useAuth from '../hooks/use-auth';
import useForm from '../hooks/use-form';
import Card from '../UI/Card/Card'
import Input from './Input'
import classes from './Login.module.css';


const Register = () => {

 const {signUpHandler} = useAuth();

 const { inputVal:fullNameValue, 
  onChangeHandler:onChangeNameHandler, 
  onBlurHandler:onBlurNameHandler, 
  resetHandler:resetNameHandler } = useForm((e) => e.length >= 8);
  const { inputVal:emailValue, 
    onChangeHandler:onChangeEmailHandler, 
    onBlurHandler:onBlurEmailHandler, 
    resetHandler:resetEmailHandler } = useForm( (e) =>  e.includes('@'));
 
   
 
  const onSubmitHandler = (e) => {

    e.preventDefault();
    signUpHandler('tahashabaan713@gmail.com', 'Taha7008', fullNameValue).then(user => alert(user.user + 'sign Up') ).catch( error => 
        alert(error.message) )

        resetNameHandler();
  }

  return (
   <Card className={classes.register}>
    <form onSubmit={onSubmitHandler}>
    <Input 
        label='full Name'
        type='text' 
        id='text' 
        placeholder='enter Full Name'
        onChangeHandler = {onChangeNameHandler}
        onBlurHandler={onBlurNameHandler}/>

    <Input 
        label='Email'
        type='email' 
        id='email' 
        placeholder='enter email'/>

       <Input 
        label='Password'
        type='password' 
        id='password'
        placeholder='enter password'/>

       <button className={classes.btn}>Register</button>
       <Link to='/login' > Sign In </Link>
    </form>
    </Card>
  )
}

export default Register;
