import React, { useContext, useState } from 'react';
import CartContext from '../../store/cart-context';
import  Modal  from '../UI/Modal/Modal';
import classes from './Cart.module.css'
import CartItem from './CartItem';
import CheckoutForm from './CheckoutForm';

const Cart = (props) => {

  const ctx = useContext(CartContext);
  const [checkForm, setCheckForm] = useState(false)

  const checkOrder = ctx.cartItem.length > 0;
  const addItemCartHandler = (item) => {
    ctx.onAddItem({...item, amount:1})
  }

  const removeItemCartHandler = (id) => {
    ctx.onRemoveItem(id)
  }

  const onOpenFormHandler = () => {
    setCheckForm(true);
  }

  const onCloseFormHandler = () => {
    setCheckForm(false);
  }

  const sendOreder = async (userdata) => {
    const response = await fetch('https://react-http-399a8-default-rtdb.firebaseio.com/order.json',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body:JSON.stringify({
        user:userdata,
        item:ctx.cartItem
      })
    })

    const data = await response.json();

  

  }

  const onConfirmHandler = (userdata) => {
    sendOreder(userdata);
  }

  const totalAmount = `${ctx.totalAmount.toFixed(2)} $`;

  return (
    <Modal>
      <ul className={classes['cart-items']}>
        {ctx.cartItem.map((item) => <CartItem
          key={item.id}
          id={item.id}
          name={item.name}
          price={item.price}
          amount={item.amount}
          onAdd={addItemCartHandler.bind(null, item)}
          onRemove={removeItemCartHandler.bind(null,item.id)}
          />)}
        </ul>
        <div className={classes.total}>
          <span>totalAmount</span>
          <span>{totalAmount}</span>
        </div>
       <div className={classes.actions}>
        <button  onClick={ctx.onHideCart}
         className={classes['.button--alt']}>
          Close
        </button>
        {checkOrder && <button className={classes.button} 
         onClick={onOpenFormHandler}>
          Order
        </button>}
      </div>
     {checkForm && <CheckoutForm onConfirm={onConfirmHandler}
     onClose={onCloseFormHandler}/>}

    </Modal>
  )
}

export default Cart
