import React, { useEffect, useState } from 'react'
import {auth} from '../../firebase';

import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, getAuth } from 'firebase/auth';
 

const useAuth =() => {

  const userHandler = async(userHandler, email, password) => {
      try{ 
        await userHandler(auth, email, password);
       } catch(error){  alert(error.meaasge)
       }
  }

  return { userHandler }
}

export default useAuth;
