import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import React, { useEffect, useState } from 'react'
import {auth} from '../../firebase/firebase';

 

 const useAuth =() => {

   const [user, setUser] = useState();

   const signUp = async(email, password, name) => {
      try{ 
        await createUserWithEmailAndPassword(auth, email, password, name);
       } catch(error)   {  alert(error.meaasge)  }
  }

  const signIn= async(email, password) => {
    try { 

      const userCredintal = await signInWithEmailAndPassword(auth, email, password);
      console.log(userCredintal.user);

     }  catch(error)   {  alert(error.meaasge)  }
}

 const logOut= async() => {
  try { signOut(auth) }  catch(error)   {  alert(error.meaasge)  }
 } 

 useEffect( () => {
  const unsubscrib = onAuthStateChanged(auth, (user) => {
    if (user) {
      console.log(user);
      setUser(user)
    }
  })

 }, []);


  return { signUp, signIn, logOut }
}

export default useAuth;
