import React, { useReducer } from 'react';
import useForm from '../hooks/use-form';
import classes from './CheckoutForm.module.css';

// const inputValue = {
//   value:'',
//   isTouching:true
// }

const inputReducer = (state, action) => {

  if ( action.type === 'INPUT_USER' ) {
    return{
      value:action.value,
      isTouching:state.isTouching
    }
  }

  if ( action.type === 'BLUR' ) {
    return{
      value:state.value,
      isTouching:true
    }
  }

  if ( action.type === 'RESET' ) {
    return{
      value:'',
      isTouching:false
    }
  }
 } 

const CheckoutForm = (props) => {
  // const notEmpty = value => value !== '';
  // const {value: firstName,
  //        hasError:firstNameError } =  useForm(notEmpty);
  const [inputState, dispatchInput] = useReducer(inputReducer, inputValue)

  const onChangeInputHandler = (e) => {
    dispatchInput({type:'INPUT_USER', value:e.target.value })
  }

  const onBlurInputHandler = (e) => {
    dispatchInput({type:'BLUR'})
  } 

  const onSubmitHandler = (e) => {
    e.preventDefault();
    console.log(inputState,firstNameIsValid)

  }
  const firstNameIsValid = inputState.value !=='';
  const firstNameIsInalid = inputState.isTouching && !firstNameIsValid
  const classe = firstNameIsInalid? `${classes.control} ${classes.invalid}`:`${classes.control}`;


  return (
    <form onSubmit={onSubmitHandler}
    className={`${classes.form} ${props.className}`}>
      <div className={classe}>
        <label htmlFor='firstName'>firstName</label>
        <input type='text' id='firstName'
           onChange={onChangeInputHandler} 
           onBlur={onBlurInputHandler}/>
      </div>

      <div className={classes.control}>
        <label htmlFor='lastName'>lastName</label>
        <input type='text' id='lastName'/>
      </div>

      <div className={classes.control}>
        <label htmlFor='email'>Email</label>
        <input  type='email' id='email'
        />
      </div>
      <div className={classes.actions}>
        <button >confirm</button>
        <button type='button'>cancel</button>
      </div>
    </form>
  )
}

export default CheckoutForm
