import React, { useState, useEffect, useContext } from 'react'
import { CardElement, PaymentElement, useElements, useStripe} from '@stripe/react-stripe-js';
import axios from '../../axios'
import classes from './CheckOut.module.css'
import CartContext from '../../store/cart-context';


const CheckOut = (props) => { 

  const [cardElement, setCardElement] = useState(null);
  const [secertKey, setSecertKey] = useState(null);

  const [sucessed, setSucessed ] = useState(false);
  const [disable, setDisable] = useState(true);
  const [error, setError ] = useState(null);
  const [loading, setLoading ] = useState(false);
  
  const stripe = useStripe();
  const elements = useElements();
  
  const {totalAmount} = useContext(CartContext)
  

  
  const submitHandle = async (e) => {
    setLoading(true);
    e.preventDefault();  

    const payload = await stripe.confirmCardPayment(secertKey,{
         payment_method:{
          card:elements.getElement(CardElement)
         }
    }).then((paymentIntent) => {
       setSucessed(true);
       setLoading(false);
       set

    })
  }

  const changeHandle = (e) => {
    setDisable(e.empty);
    setError(error? error.message:'');

    // setCardElement(e.target.value);
    // console.log(cardElement);
  }

  useEffect(() => {
    const getClientSecret = async () => {

     const response = await axios.post({
        method:'post',
        url:`/payment/create?amount=${totalAmount * 100}`,
      })

      setSecertKey(response.data.clientSecret);
      return response;
    }
    getClientSecret();
  
  }, [totalAmount])

  
  return (
  
      <form onSubmit={submitHandle} className={classes.form}>
       
        {/* <PaymentElement/> */}
        {/* <PaymentElement/> */}
        <CardElement className={classes.cardElement}/>
        <input  onChange={changeHandle}/>
        <button disabled={disable || loading || sucessed}>
           {loading? "laoding...":"BuyNow"}
        </button>

        {error && <p>{error}</p>}
       </form>
   
  )
}

export default CheckOut
