import React, { useEffect, useState } from 'react'
import Card from '../../UI/Card/Card'
import MealsItem from '../MealsItem/MealsItem'
import classes from './MealsList.module.css'

const MealsList = (props) => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState(null)

  const fetchMeals = async () => {
    try{
     setIsLoading(true)
     setError(null)
     const response = await fetch('https://react-http-399a8-default-rtdb.firebaseio.com/meals.json');

     if (!response.ok){throw new Error('somthing wrong')}
     const data = await response.json();

     const loadingMeals = [];

     for (const key in data) {
        loadingMeals.push({
          id: key,
          name: data[key].name,
          description: data[key].description,
          price: data[key].price
      })

    }
    setMeals(loadingMeals);
    setIsLoading(false)
  }catch(error){
      setError(error.massage)
    }
  }

  useEffect(() => {
    fetchMeals()
  },[])

  let content = <p>found no meals</p>

  if(meals.length > 0){
    content = meals.map((meal) => <MealsItem
    key={meal.id}
    id={meal.id}
    name={meal.name}
    description={meal.description}
    price={meal.price} 
   />)
  }

  if(isLoading){
    content
  }

  console.log(meals)
  return (
    <Card className={classes.list}>
      <ul>
        {!isLoading && meals.length > 0 && meals.map((meal) => <MealsItem
          key={meal.id}
          id={meal.id}
          name={meal.name}
          description={meal.description}
          price={meal.price} 
         />)}
         {!isLoading && meals.length === 0 && !error && <p>found no meals</p>}
         { isLoading &&!error && <p>loading...</p>}
         { !isLoading && error && <p>error</p>}
      </ul>
    </Card>
    
  )
}

export default MealsList
