import{ createContext, useState } from 'react';


 const CartContext = createContext({
  cartItem:[],
  showCart:false,
  onAddItem:(item) => { },
  onRemoveItem:(id) => {},
  onShowCart:() => {},
  onHideCart: () => {}
 })

export const CartContextProvider = (props) => {
  const [cartItem, setCartItem] = useState([]);
  const [showCart, setShowCart] = useState(false)

  const addItemToCartHandler = (items) => {
    setCartItem(preItem => {

      const existingCartItemIndex = preItem.findIndex(item => item.id === items.id);

      const existingCartItem = preItem[existingCartItemIndex];

      let updateItems;

      if(existingCartItem){
       existingCartItem.amount += items.amount;
       const updateItem = {
        ...existingCartItem
        }

     updateItems =[...preItem];
     updateItems[existingCartItemIndex] = updateItem
      }
      else{
        updateItems = [...preItem,items]
      }
    return updateItems  })   
 }
 const removeItemToCartHandler = (id) => {
  setCartItem(preItem => {

    const existingCartItemIndex = preItem.findIndex(item => item.id === id)
    const existingCartItem = preItem[existingCartItemIndex]

    let updateItems;
    if(existingCartItem){
      existingCartItem.amount -= 1;
      const updateItem={
        ...existingCartItem
      }
    }
    // else { updateItems=[...preItem] }

    return updateItems;
  })

  
}

console.log("cartItem:",cartItem)


 const onShowCartHandler = () => {
   setShowCart(true)
 }

 const onHideCartHandler = () => {
   setShowCart(false)
 }
 return <CartContext.Provider value={{
  cartItem:cartItem,
  showCart:showCart,
  onAddItem:addItemToCartHandler,
  onRemoveItem:removeItemToCartHandler,
  onShowCart:onShowCartHandler,
  onHideCart:onHideCartHandler
 }}>
  {props.children}
 </CartContext.Provider>
}

export default CartContext
