import { useState, useEffect, useReducer } from "react";
import CartContext from './cart-context';


const intialProduct ={
  products:[],
  totalAmount:0
}

const productReducer = (state, action) => {

  if (action.type === 'Add') {

    const updateTotalAmount = state.totalAmount + action.payload.quantity * action.payload.price;

    const existingProductIndex = state.products.findIndex(
      product => product.id === action.payload.id)
    const existingProduct = state.products[existingProductIndex];

    let updateProducts;
    if (existingProduct) {
      let updateProduct = {
        ...existingProduct,
        
  
      }

    }

    else {
        updateProducts = state.products.concat(action.payload);
     }
    return {
      products: updateProducts,
      totalAmount: updateTotalAmount
    }
  }

  if (action.type === 'Remove'){
    
  }

  return {intialProduct}

}

const CartProvider = (props) => {

  const [stateProduct, dispatchProduct] = useReducer(productReducer, intialProduct);

  const [items, setItems] = useState({
    products:[],
    total:0,
    skip:0,
    limit:0,
  });
  
  const fetchData = async () => {
   const response = await fetch('https://dummyjson.com/products');
   const data = await response.json();
   setItems(data);
  }

  useEffect(() => {
    fetchData();
  },[])

  const AddProductHandler = (payload) => {
    dispatchProduct({type:'Add',payload})
  }



  return (
   <CartContext.Provider value={{
    items,
    products:stateProduct.products,
    totalAmount:stateProduct.totalAmount,
    onAddProduct:AddProductHandler
    }}>
         {props.children}
   </CartContext.Provider>)
}

export default CartProvider;
