import "./ExpenselItem.css";
import Expendelitem from "./Expenselitem.js";
import "./Expense.css";
import Cards from "../UI/Cards.js";
import React , { useState } from "react";
import ExpenseFilter from "./ExpenseFilter";

const Expense = (props) => {
  const [filterYear, setFilterYear] = useState('2019');
  // console.log(props.data[2].data);
  const selectYear = (year) => {
    setFilterYear(year);
  }
  const filterDateHandel = props.data.filter(filter => filter.data.getFullYear().toString() 
  === filterYear )
  const contentExpense = () => {
   filterDateHandel.length === 0 ? 
    <p style={{color:"white"}}>Not Found Expense</p> : 
   filterDateHandel.map( expense =>   
    <Expendelitem 
     key= {expense.id}
     title={expense.title} data={expense.data}
     amount={expense.amount} 
     />)
    }
  return (
    <div>
    <Cards className='expense'>
      <ExpenseFilter selected={filterYear} 
                 onSelectYear={selectYear}
     />
     {contentExpense}
   </Cards>
   </div>
      )
  }
export default Expense;
