    //     props.onSumbitItem({...items, quantity:  +(numberRef.current.value)})
    //   }

    //   else {
    //     item.quantity +=1;
    //   }
    // })}

    }
    


  return (
    <form onSubmit={onSubmitHandler}
      className={classes.form}>
      <div>
      <label htmlFor='number'>Amount</label>
      <input 
        ref={numberRef}
        max='5'
        min='1'
        type='number'
        id='number'
        defaultValue='1'/>
      </div> 
     <button > +Add </button>
    </form>
  )
}

export default MealsItemForm
