import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {auth} from '../../firebase/firebase';

 

 const useAuth =() => {

   const [user, setUser] = useState();
   const [error, setError] = useState(false);

   const navigate = useNavigate();
   const authHandler = (userHandler,email, password ) => {
       return userHandler(auth,email, password )
   }
   
   const signUp = async(email, password, name) => {
      try{ 
        await createUserWithEmailAndPassword(auth, email, password, name);
        navigate('/login')

       }   catch(error)  {  
        setError(error.meaasge) 
        navigate('/register')    }
  }

  const signIn= async(email, password) => {

    try { 

      await signInWithEmailAndPassword(auth, email, password);
      navigate('/')

     }  catch(error){  alert(error.meaasge)  }
}

 const logOut= async() => {

  try { 

        await signOut(auth) 
        navigate('/login') 

       }  catch(error)   {   setError(error.message)  }

 } 

 useEffect( () => {

  const unsubscrib = onAuthStateChanged(auth, (user) => {

    if (user) { setUser(user);  }

    else { setUser(null) }

  })

  return unsubscrib;

 }, []);


  return { user, error, signUp, signIn, logOut }
}

export default useAuth;
