import React,{useContext} from 'react';
import CartContext from '../../../store/cart-context';
import MealsItemForm from './MealsItemForm';
import classes from './MealsItem.module.css';

const MealsItem = (props) => {

  // const price = `${+props.price.toFixed(2)}$`;
  const ctx = useContext(CartContext);

  // const sendRequest = async(dataRequst) => {
  //   const response = await fetch ('https://react-http-399a8-default-rtdb.firebaseio.com/mealsProduct.json',{
  //     method:'POST',
  //     body:JSON.stringify(dataRequst),
  //     headers : {
  //       'Content-Type' : 'application/json'
  //     }
  //   })

  //   const data = await response.json();

  //   console.log(data)
    
  // }

  const onAddToCartHandler = (amount) => {

    ctx.onAddItem({
        id:props.id,
        name:props.name,
        price:props.price,
        amount
    })

    // sendRequest({
    //   id:props.id,
    //   name:props.name,
    //   price:props.price,
    //   amount })

  }

    return (
   <li className={classes.meal}>
    <section className={classes.left}>
      <h2>{props.name}</h2>
      <p>{props.description}</p>
      <h4>{props.price} </h4>
    </section>
    <section >
     <MealsItemForm onAddToCart={onAddToCartHandler}/>
    </section>
    </li>
  )
}

export default MealsItem
