import { async } from '@firebase/util';
import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {auth} from '../../firebase/firebase';

 

 const useAuth =() => {

   const [user, setUser] = useState();
   const [error, setError] = useState(false);
   const [isLogin, setIsLogin] = useState(true);

   const navigate = useNavigate();

   const authHandler =async (userHandler,email, password,navigate ) => {
      try { 
       await userHandler(auth,email, password );
       navigate(navigate);

    } catch(error) {
      setError()
       }
   }
   
   const signUp = async(email, password, name) => {
      try{ 
        await createUserWithEmailAndPassword(auth, email, password, name);
        navigate('/login')

       }   catch(error)  {  
        setError(error.meaasge) 
        alert(error.message)
        navigate('/register')    
      }
  }

  const signIn= async(email, password) => {

    try { 

      await signInWithEmailAndPassword(auth, email, password);
      navigate('/')
      setIsLogin(false);

     }  catch(error) {  
        alert(error.message)   
      // setIsLogin(false);  
    }

}

 const logOut= async() => {

  try { 

        await signOut(auth) 
        navigate('/login') 

       }  catch(error)   {   setError(error.message)  }

 } 

 useEffect( () => {

  const unsubscrib = onAuthStateChanged(auth, (user) => {

    if (user) { setUser(user);  }

    else { setUser(null) }

  })

  return unsubscrib;

 }, []);


  return { user, error, isLogin, signUp, signIn, logOut }
}

export default useAuth;
