import React, { useEffect, useState } from 'react'
import Card from '../../UI/Card/Card'
import MealsItem from '../MealsItem/MealsItem'
import classes from './MealsList.module.css'

const MealsList = (props) => {

  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState(null)

  // const fetchMeals = async () => {
   
  //   try {

  //    setError(null)
  //    setIsLoading(true)

  //    const response = await fetch('https://react-http-399a8-default-rtdb.firebaseio.com/meals.json');

  //    if (!response.ok) {
  //     throw new Error('something went wrong!')
  //   }

  //   console.log(response)
  //    const data = await response.json();
  //    console.log(data)

  //    const loadingMeals = [];

  //    for (const key in data) {
  //       loadingMeals.push({
  //         id: key,
  //         name: data[key].name,
  //         description: data[key].description,
  //         price: data[key].price
  //     })

  //   }
  //   setMeals(loadingMeals);
  // } catch(error){
  //     setError(error.massage)
  //   }
  //   setIsLoading(false)
  // }

  const  fetchMeals = async () => {
    try {
      setError(null)
      setIsLoading(true)

      const response =  await fetch('https://order-food-9c059-default-rtdb.firebaseio.com/meals.json');

      if (!response.ok)  throw new Error(true)

      const data = await response.json();
      console.log(data)

      const loadingMeals = [];

      for (const key in data) {
        loadingMeals.push({
          id:key,
          price:data[key].price,
          name:data[key].name,
          description:data[key].description
        })
      }
      setMeals(loadingMeals)

    } catch(error){
      setError('somthing Went Wrong!')
    }

    setIsLoading(false)
  }

  useEffect(() => {
    fetchMeals();
  },[])


  
  const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ]
  let content = <p>found no meals</p>
  
    if (meals.length > 0)
     content = meals.map((meal) => <MealsItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price} />)
  



  if(isLoading){
    content = <p>loading...</p>
  }

  if( error){
    content = <p> {error} </p>
  }

  return (
    <Card className={classes.list}>
      <ul>
     { content}
      </ul>
    </Card>
    
  )
}

export default MealsList
