import React, { useReducer } from 'react';
import useForm from '../hooks/use-form';
import classes from './CheckoutForm.module.css';

const CheckoutForm = (props) => {
  const notEmpty = value => value !== '';
  const {value: firstName,
         inputIsValid:firstNameIsValid,
         hasError:firstNameError,
         onChangeInpuValuetHandler:onChangeFirstNameHandler,
         onBlurInputValueHandler:onBlurFirstNameHandler,
         reset:firstNameReset } =  useForm( notEmpty );

         const {value: lastName,
          inputIsValid:lastNameIsValid,
          hasError:lastNameError,
          onChangeInpuValuetHandler:onChangeLasttNameHandler,
          onBlurInputValueHandler:onBlurLastNameHandler,
          reset:lastNameReset } =  useForm( notEmpty );

          const {value: email,
            inputIsValid:emailIsValid,
            hasError:emailError,
            onChangeInpuValuetHandler:onChangeEmailHandler,
            onBlurInputValueHandler:onBlurEmailHandler,
            reset:emailReset } =  useForm( value => value.includes('@') );

  const FirstNameClass = firstNameError? `${classes.control} ${classes.invalid}`:`${classes.control}`;
  const lastNameClass = lastNameError? `${classes.control} ${classes.invalid}`:`${classes.control}`;
  const emailClass = emailError? `${classes.control} ${classes.invalid}`:`${classes.control}`;

  const formValided = firstNameIsValid && lastNameIsValid && emailIsValid

  let formIsValid = false;
  if(formValided){
    formIsValid = true;
  }

  const onSubmitHandler = (e) => {
    e.preventDefault();

    if(!formValided){return }

    props.onConfirm({
      firstName:firstName
    })

    firstNameReset();
    lastNameReset();
    emailReset()
  }


  return (
    <form onSubmit={onSubmitHandler}
     className={`${classes.form} ${props.className}`}>
      <div className={FirstNameClass}>
        <label htmlFor='firstName'>firstName</label>
        <input type='text' id='firstName' 
           value={firstName}
           onChange={onChangeFirstNameHandler} 
           onBlur={onBlurFirstNameHandler}/>
      </div>

      <div className={lastNameClass}>
        <label htmlFor='lastName'>lastName</label>
        <input type='text' id='lastName'
        value={lastName}
        onChange={onChangeLasttNameHandler}
        onBlur={onBlurLastNameHandler}
        />
      </div>

      <div className={emailClass}>
        <label htmlFor='email'>Email</label>
        <input  type='email' id='email'
        value={email}
        onChange={onChangeEmailHandler}
        onBlur={onBlurEmailHandler}
        />
      </div>
      <div className={classes.actions}>
        <button disabled={!formIsValid}>confirm</button>
        <button type='button' onClick={props.onClose}>
          cancel
        </button>
      </div>
    </form>
  )
}

export default CheckoutForm
