import React, { useRef, useContext } from 'react'
import CartContext from '../../../store/cart-context';
import classes from './ProductItemForm';

const ProductItemForm = ({product}) => {
  //  const stock = 
  //  console.log(stock)

  const ctx = useContext(CartContext);

  const addToCartHandler = () => {

    const quantity = +(selectRef.current.value);
    const data = {
       ...product,
       quantity
      }

      ctx.onAddProduct(data);
      localStorage.setItem('cart', JSON.stringify(data));
 }
  
  const inpRef = useRef('');
  const selectRef = useRef(1);

  const onSubmitHandler = (e) => {

    e.preventDefault();
  
    addToCartHandler();
  }
  let option = [product.stock];
  console.log(option);

 

  return (
    <form onSubmit={onSubmitHandler}>

     <div className={classes.controls}>
       <label htmlFor='number'>Quantity</label>
       <input 
        type='number' 
        id='number'
        defaultValue='1'
        ref={inpRef}  />
     </div>

     <div className={classes.controls}>
     
       <label htmlFor='select'>Quantity</label>
        <select id='select' ref={selectRef}>
          {option.map}
         
          <option value={2}>2</option>
          <option value={2}>2</option>
          <option value={2}>2</option>
          <option value={2}>2</option>
          <option value={2}>2</option>
        </select>

     </div>

     <div className={classes.actions}>
       <button>Add to Cart</button>
     </div>
     
    </form>
  )
}

export default ProductItemForm;
