import {useReducer} from 'react'

  const inputValue = {
    value:'',
    isTouching:true
  }
  
  const inputReducer = (state, action) => {
  
    if ( action.type === 'INPUT_USER' ) {
      return{
        value:action.value,
        isTouching:state.isTouching
      }
    }
  
    if ( action.type === 'BLUR' ) {
      return{
        value:state.value,
        isTouching:true
      }
    }
  
    if ( action.type === 'RESET' ) {
      return{
        value:'',
        isTouching:false
      }
    }
   } 

   const useForm = (enterInputIsValid) => {
    const [inputState, dispatchInput] = useReducer(inputReducer, inputValue)
  
    const onChangeInpuValuetHandler = (e) => {
      dispatchInput({type:'INPUT_USER', value:e.target.value })
    }
  
    const onBlurInputValueHandler = (e) => {
      dispatchInput({type:'BLUR'})
    } 

    const reset = () => {
      dispatchInput({type:'RESET'})
    }

    const inputIsValid  = enterInputIsValid(inputState.value) 
    const hasError = inputState.isTouching && !inputIsValid;

    return {
      value:inputState.value,
      hasError,
      onChangeInpuValuetHandler,
      onBlurInputValueHandler,
      reset,
      inputIsValid
    }
 
  }



export default useForm;
