import React, { useRef, useState } from 'react'
import classes from './MealItem.module.css'


const MealItem = (props) => {
  
  const [items, setItems] = useState([]);
  const amountRef = useRef(1)
  console.log(amountRef.current)

  const onSubmitHandler = (e) => {
    console.log(e.target)
    e.preventDefault();
    addItemHandler(props.id, 1)
  }

  const addItemHandler = (id, amount) => {

      setItems ((preState) => {
        const indexItem = preState.findIndex(state => state.id===id)
      
        const findItme = preState[indexItem];

         let updateItems = items ;

        if (findItme) {

          const updateItem = {
            ...findItme,
            amount:findItme.amount + +amountRef.current.value
          }

          updateItems = [...preState];
          updateItems[indexItem] = updateItem; 

        }

        else {
             updateItems.push({
              id:props.id,
              name:props.name,
              description:props.description,
              price:props.price,
              amount:+amountRef.current.value
            }) }

            return updateItems;
          
      })
      
  }

  console.log(items)
  

  return (
    <div 
     className={classes.wrapper}>

      <section className={classes.left}>
        <h3>{props.name}</h3>
        <h5>{props.price}</h5>
        <p>{props.description}</p>
      </section>

      <section className={classes.right}>

        <form onSubmit={onSubmitHandler}>
          <label>Amount</label>
          <input 
           type='number'
           defaultValue='1'
           min='1'
           max='5'
          //  value={1}
           ref={amountRef}  />
          <button>Add</button>
        </form>

      </section>
    </div>
  )
}

export default MealItem
