import React ,{useState} from "react";
import "./NewExpense.css";
 const NewEXpenseForm = (props) =>{
   // multiple state
  const [title , setTitle] = useState("");
  const [amount , setAmount] =useState("");
  const [date , setDate] = useState("");
  const [hideForm , setHideForm] = useState(true);

  // one state
  // const [input , setInput] = useState({
  //   title:"title",
  //   amount:"amount",
  //   date:"date"
  // })
   const TitleChangeHandler = (e)=>{
     setTitle(e.target.value);
    //  console.log(input.title);
    //  setInput(preState=>{
    //    return {...preState,title:e.target.value};
    //  } );
   }
   const AmountChangerHandler = (e)=>{ 
     setAmount(e.target.value);
    // console.log(input.amount);
    // setInput((preState)=>{
    //   return {...preState , amount:e.target.value}
    // })
  }
  const DateChangerHandler = (e)=>{
    setDate(e.target.value);
    // setInput((preState)=>{
    //   return {...preState , date:e.target.value}
    // })
     
  }
  const SubmitHandler= (event)=>{
    // const input=document.querySelectorAll("input");
    event.preventDefault();
   const newExpense={
     title:title,
     amount:amount,
     date:new Date(date)
   } ;
   props.onSaveExpenseData(newExpense);
   setTitle('');
   setAmount('');
   setDate('');
      // input.forEach(input=>{
      //   input.value="";
      // })
  }
      return (
     <form onSubmit={SubmitHandler} >
      <div className={`new-expense__contorls
       lightform ${hideForm ? "hide-lightform" :""}`}>
         <div className="new-expene__control">
           <label>title</label>
           <input 
            type='text' 
            value={title}
            onChange={TitleChangeHandler}/>
         </div>
         <div className="new-expene__control">
           <label>amount</label>
           <input 
           type='number' 
           min="0.01" 
           step="0.01" 
           value={amount}
           onChange={AmountChangerHandler}/>
         </div>
         <div className="new-expene__control">
           <label>date</label>
           <input 
           type='date' 
           min="2019-01-01" 
           max="3024-12-31" 
           value={date}
           onChange={DateChangerHandler}/>
         </div>
       </div>
       <div className="new-expense__actions">
           <button type="submit" 
           onClick={() =>{
            setHideForm(false)
          }}>  
              add new expense 
           </button>
           <button type="submit"
            onClick={() =>{
              setHideForm(true)
            }}>
              cancle 
           </button>
         </div>
     </form>
   
   )
 }
  export default NewEXpenseForm;
