  //   }
  //   setMeals(loadingMeals);
  // } catch(error){
  //     setError(error.massage)
  //   }
  //   setIsLoading(false)
  // }

  const  fetchMeals = async () => {
    try {
      setError(null)
      setIsLoading(true)

      const response =  await fetch('https://product-http-a3e09-default-rtdb.firebaseio.com/meals.json');
      console.log(response)

      if (!response.ok)  throw new Error(true)

      const data = await response.json();

      console.log(data)

      const loadingMeals = [];

      for (const key in data) {
        loadingMeals.push({
          id:key,
          price:data[key].price,
          name:data[key].name,
          description:data[key].description
        })
      }
      setMeals(loadingMeals)

    } catch(error){
      setError('somthing Went Wrong!')
    }

    setIsLoading(false)
  }

  useEffect(() => {
    fetchMeals();
  },[])


  
  const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ]
  let content = <p>found no meals</p>
  const filterMeals = meals.length > 0? meals : DUMMY_MEALS;
  
    content = meals.map((meal) => <MealsItem
    key={meal.id}
    id={meal.id}
    name={meal.name}
    description={meal.description}
    price={meal.price} 
   />)
  



  if(isLoading){
    content = <p>loading...</p>
  }

  if( error){
    content = <p> {error} </p>
  }

  console.log(meals)
  return (
    <Card className={classes.list}>
      <ul>
      DUMMY_MEALS.map((meal) => <MealsItem
       key={meal.id}
       id={meal.id}
       name={meal.name}
       description={meal.description}
      price={meal.price} 
   />)
      </ul>
    </Card>
    
  )
}

export default MealsList
